import React, {Component} from 'react';
import logo from './logo.svg';
import './App.css';
import './dist/css/bootstrap.min.css';

class AppHeader extends Component {
    constructor(props) {
        super(props);
        this.state = {
            Title : "Laporan Praktikum Web Design : CSS",
            Nama : "Adam Arthur Faizal",
            NIM : "M3119001",
            Kelas : "TI A 2019"
        }
    }
    render() {
        return (
            <div className="App-header">
                <img src={logo} className="App-logo" alt="logo" />
                <h1 className="font-weight-bold">{this.state.Title}</h1>
                <h3>{this.state.Nama}</h3>
                <h4>{this.state.NIM} - {this.state.Kelas}</h4>
                <h4 className="font-weight-bold">Modul CSS</h4>
                <h5 className="font-italic">React version : {React.version}</h5>
            </div>
        );
    }
}

class AppMain extends Component {
    constructor(props) {
        super(props);
    }
    render() {
        return (
            <div className="App-main d-xl-flex justify-content-around">
                <section className="alert alert-secondary">
                    <ol>
                        <li><a href="">Width & Height</a></li>
                        <li><a href="">Margin</a></li>
                        <li><a href="">Padding</a></li>
                        <li><a href="">Border</a></li>
                        <li><a href="">Format Text & Font</a></li>
                    </ol>
                </section>
                <section className="alert alert-secondary">
                    <ol>
                        <li><a href="">Background</a></li>
                        <li><a href="">Pseudo-Class</a></li>
                        <li><a href="">Linear-Gradient</a></li>
                        <li><a href="">Box Shadow</a></li>
                        <li><a href="">Nav</a></li>
                    </ol>
                </section>
                <section className="alert alert-secondary">
                    <ol>
                        <li><a href="">Aside</a></li>
                        <li><a href="">Card</a></li>
                        <li><a href="">Button</a></li>
                        <li><a href=""></a></li>
                        <li><a href=""></a></li>
                    </ol>
                </section>
            </div>
        );
    }
}

export default AppHeader;
